    def maxSum(self, nums: List[int], k: int, m: int) -> int:
        n = len(nums)
        if n < k * m:
            return -1

        # Initialize the dp array to store the maximum sum of k subarrays of length m
        dp = [0] * (n + 1)

        # Calculate the sum of the first k elements
        for i in range(k):
            dp[i + 1] = dp[i] + nums[i]

        # Iterate through the array starting from the k-th element
        for i in range(k, n):
            # Update the dp array by considering the maximum sum of k subarrays of length m
            dp[i + 1] = max(dp[i + 1], dp[i] + nums[i] - dp[i - k + 1])

        # Return the maximum sum of k subarrays of length m
        return dp[n]

