class Solution:
    def lenOfVDiagonal(self, grid: List[List[int]]) -> int:
        def dfs(i, j, direction):
            if i < 0 or j < 0 or i >= len(grid) or j >= len(grid[0]) or grid[i][j] != 1:
                return 0
            if direction == 0:
                return 1 + dfs(i + 1, j + 1, 0)
            elif direction == 1:
                return 1 + dfs(i + 1, j - 1, 1)
            elif direction == 2:
                return 1 + dfs(i - 1, j + 1, 2)
            else:
                return 1 + dfs(i - 1, j - 1, 3)

        max_length = 0
        for i in range(len(grid)):
            for j in range(len(grid[0])):
                if grid[i][j] == 1:
                    max_length = max(max_length, dfs(i, j, 0))
                    max_length = max(max_length, dfs(i, j, 1))
                    max_length = max(max_length, dfs(i, j, 2))
                    max_length = max(max_length, dfs(i, j, 3))
        return max_length
