class Solution:
    def maxFreeTime(self, eventTime: int, k: int, startTime: List[int], endTime: List[int]) -> int:
        # Sort the meetings by their start time
        meetings = sorted(zip(startTime, endTime))

        # Initialize variables to keep track of the current free time and the maximum free time
        current_free_time = 0
        max_free_time = 0

        # Iterate through the sorted meetings
        for start, end in meetings:
            # If the current meeting starts before the current free time, update the current free time
            if start < current_free_time:
                current_free_time = end - start
            # If the current meeting ends after the current free time, update the maximum free time
            elif end > current_free_time:
                max_free_time = max(max_free_time, end - current_free_time)
                current_free_time = end

        # If there are still meetings left to reschedule, update the maximum free time
        if k > 0:
            max_free_time = max(max_free_time, eventTime - current_free_time)

        return max_free_time

