        index = [i for i in range(n) if nums[i] == nums[queries[0]]]
        for i in range(len(queries)):
            if queries[i] in index:
                ans[i] = 0
            else:
                left = bisect.bisect_left(index, queries[i])
                right = bisect.bisect_right(index, queries[i])
                if left == 0:
                    ans[i] = n - index[-1] - 1
                elif right == len(index):
                    ans[i] = index[0] - 1
                else:
                    ans[i] = min(index[left] - queries[i], queries[i] - index[right - 1])
        return ans
