    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        n = len(nums1)
        ans = [0] * n
        heap1 = []
        heap2 = []

        for i in range(n):
            heapq.heappush(heap1, -nums1[i])
            heapq.heappush(heap2, -nums2[i])

        for i in range(n):
            while heap1 and len(heap1) > k:
                heapq.heappop(heap1)
            while heap2 and len(heap2) > k:
                heapq.heappop(heap2)

            if heap1 and heap2:
