class Solution:
    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        n = len(nums1)
        res = [0] * n
        heap1 = []
        heap2 = []
        for i in range(n):
            heapq.heappush(heap1, -nums1[i])
            heapq.heappush(heap2, -nums2[i])
        for i in range(n):
            if nums1[i] < nums1[i-1]:
                heapq.heappush(heap1, -nums1[i])
                heapq.heappop(heap2)
            if len(heap2) > k:
                heapq.heappop(heap2)
            res[i] = -heap2[0]
        return res
