    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        n = len(nums1)
        ans = [0] * n

        # Sort nums1 in descending order
        nums1.sort(reverse=True)

        # Initialize a max heap to store the largest k elements from nums2
        max_heap = []

        for i in range(n):
            # Add the largest k elements from nums2 to the max heap
            for j in range(k):
                if j < len(nums2):
                    heapq.heappush(max_heap, -nums2[j])

            # If the max heap is not empty, pop the largest element from the heap
            if max_heap:
                ans[i] = -heapq.heappop(max_heap)

        return ans

class Solution:
    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        n = len(nums1)
        ans = [0] * n
        heap = []
        for i in range(n):
            heapq.heappush(heap, (-nums2[i], i))
            if len(heap) > k:
                heapq.heappop(heap)
        for i in range(n):
            if nums1[i] < nums1[heap[0][1]]:
                ans[i] = -heapq.heappop(heap)[0]
                heapq.heappush(heap, (-nums2[i], i))
        return ans

