class Solution:
    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        n = len(nums1)
        ans = [0] * n
        nums1.sort()
        nums2.sort()
        i = n - 1
        j = n - 1
        while i >= 0 and j >= 0 and k > 0:
            if nums1[i] < nums2[j]:
                ans[i] = max(ans[i], nums2[j])
                k -= 1
                j -= 1
            else:
                i -= 1
        while i >= 0 and k > 0:
            ans[i] = max(ans[i], nums2[j])
            k -= 1
            i -= 1
        return ans

class Solution:
    def findMaxSum(self, nums1: List[int], nums2: List[int], k: int) -> List[int]:
        n = len(nums1)
