class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: (x[0], x[1]))
        left = 0
        right = 0
        total_area = 0
        for x, y, l in squares:
            while right < len(squares) and squares[right][0] <= x + l:
                total_area += (squares[right][1] - y) * l
                right += 1
            if total_area == 0:
                return y
            left = right
        return y
