class Solution:
    def separateSquares(self, squares: List[List[int]]) -> float:
        squares.sort(key=lambda x: (x[0], x[1]))
        area = 0
        for x, y, l in squares:
            area += l * l
        total_area = area
        for x, y, l in squares:
            area -= l * l
            while y > 0:
                if area == total_area:
                    return y / 1.0
                y -= 1
                area += y * y
        return 0.0
