class Solution:
    def minimumCost(self, nums: List[int], cost: List[int], k: int) -> int:
        n = len(nums)
        dp = [[0] * (n + 1) for _ in range(n + 1)]

        for i in range(1, n + 1):
            for j in range(1, n + 1):
                dp[i][j] = float('inf')
                for l in range(i):
                    dp[i][j] = min(dp[i][j], dp[l][j - 1] + (nums[i - 1] + nums[l - 1] + (i - l) * k) * (cost[i - 1] + cost[l - 1]))

        return dp[n][n]
