class Solution:
    def maxSubstringLength(self, s: str, k: int) -> bool:
        n = len(s)
        if n < k:
            return False

        # Create a set of all characters in the string
        char_set = set(s)

        # If the number of unique characters is less than k, it's impossible to select k disjoint special substrings
        if len(char_set) < k:
            return False

        # Create a dictionary to store the frequency of each character in the string
        char_freq = {}
        for char in s:
            if char in char_freq:
                char_freq[char] += 1
            else:
                char_freq[char] = 1

        # If any character appears more than k times, it's impossible to select k disjoint special substrings
        for freq in char_freq.values():
            if freq > k:
                return False

        # If all characters appear at most k times, it's possible to select k disjoint special substrings
        return True
