class Solution:
    def maxSubstringLength(self, s: str, k: int) -> bool:
        n = len(s)
        dp = [[0] * 26 for _ in range(n)]
        for i in range(n):
            for j in range(i+1, n):
                for c in s[i:j]:
                    dp[i][ord(c) - ord('a')] += 1
        dp[0][ord(s[0]) - ord('a')] = 1
        for i in range(1, n):
            for j in range(26):
                dp[i][j] = dp[i-1][j] + (dp[i][j] == 0)
        return dp[n-1][0] >= k
