class Solution:
    def maxSubstringLength(self, s: str, k: int) -> bool:
        n = len(s)
        if k == 0:
            return True

        # Create a dictionary to store the last occurrence of each character
        last_occurrence = {}
        for i in range(n):
            last_occurrence[s[i]] = i

        # Initialize variables to keep track of the maximum length of a special substring
        max_length = 0

        # Iterate through the string to find the maximum length of a special substring
        for i in range(n):
            for j in range(i + 1, n):
                # Check if the substring is special
                if all(s[i] not in s[k:j] for k in range(i + 1, j)):
                    # Update the maximum length of a special substring
                    max_length = max(max_length, j - i)

        # Check if the maximum length of a special substring is greater than or equal to k
        return max_length >= k
