class Solution:
    def maxProduct(self, nums: List[int], k: int, limit: int) -> int:
        nums.sort()
        n = len(nums)
        dp = [[-1] * (k + 1) for _ in range(n + 1)]

        def dfs(i, s):
            if i == n:
                return 0 if s == 0 else -float('inf')
            if dp[i][s] != -1:
                return dp[i][s]
            dp[i][s] = max(dfs(i + 1, s), dfs(i + 1, s + nums[i]) if abs(nums[i]) <= limit else -float('inf'))
            return dp[i][s]

        return dfs(0, k)
