class Solution:
    def maxProduct(self, nums: List[int], k: int, limit: int) -> int:
        nums.sort()
        n = len(nums)
        dp = [[-1] * (k + 1) for _ in range(n + 1)]

        def dfs(i, sum, product):
            if i == n:
                return 0 if sum != k else product
            if dp[i][sum] != -1:
                return dp[i][sum]

            max_product = -1
            for j in range(i, n):
                if sum + nums[j] > k:
                    break
                if abs(nums[j]) > limit:
                    continue
                max_product = max(max_product, dfs(j + 1, sum + nums[j], product * nums[j]))
            dp[i][sum] = max_product
            return dp[i][sum]

        return dfs(0, 0, 1)
