class Solution:
    def maxDistance(self, side: int, points: List[List[int]], k: int) -> int:
        points.sort(key=lambda x: x[0])
        left, right = 0, side
        while left < right:
            mid = (left + right) // 2
            if self.is_valid(points, mid, k):
                left = mid + 1
            else:
                right = mid
        return left - 1

    def is_valid(self, points, mid, k):
        points.sort(key=lambda x: x[1])
        for i in range(k - 1):
            if points[i][0] + mid < points[i + 1][0]:
                return False
        return True
