class Solution:
    def permute(self, n: int, k: int) -> List[int]:
        def generate_permutations(n, k):
            if n == 1:
                return [1]
            else:
                prev_permutations = generate_permutations(n-1, k)
                result = []
                for i in range(n):
                    if i % 2 == 0:
                        for perm in prev_permutations:
                            result.append(perm * 10 + i + 1)
                    else:
                        for perm in prev_permutations:
                            result.append(perm * 10 + n - i)
                return result

        permutations = generate_permutations(n, k)
        if len(permutations) < k:
            return []
        else:
            return permutations[k-1]
