    def permute(self, n: int, k: int) -> List[int]:
        if k > factorial(n):
            return []

        def get_permutation(n, k):
            if n == 1:
                return [1]

            # Find the index of the first odd number
            odd_index = next((i for i in range(n) if i % 2 != 0), None)

            # If there are no odd numbers, return the permutation of the remaining even numbers
            if odd_index is None:
                return get_permutation(n - 1, k)

            # Calculate the number of permutations with odd numbers before the odd index
            odd_count = (n - 1) // 2

            # If k is less than or equal to the number of permutations with odd numbers before the odd index, return the permutation of the remaining even numbers
            if k <= odd_count:
                return get_permutation(n - 1, k) + [odd_index + 1]

            # Otherwise, return the permutation of the remaining even numbers followed by the odd number
            return get_permutation(n - 1, k - odd_count) + [odd_index + 1]

        return get_permutation(n, k)

