class Solution:
    def numOfUnplacedFruits(self, fruits: List[int], baskets: List[int]) -> int:
        from collections import Counter
        counter = Counter(fruits)
        basket = Counter(baskets)
        res = 0
        for fruit, count in counter.items():
            if fruit not in basket:
                res += count
            else:
                if basket[fruit] >= count:
                    continue
                else:
                    res += count - basket[fruit]
                    basket[fruit] = count
        return res
