class Solution:
    def longestPalindrome(self, s: str, t: str) -> int:
        # Initialize the length of the longest palindrome
        longest_palindrome = 0

        # Iterate over all possible lengths of the palindrome
        for length in range(1, len(s) + len(t) + 1):
            # Iterate over all possible starting positions of the palindrome
            for start in range(len(s) - length + 1):
                # Check if the substring from s and t forms a palindrome
                if s[start:start+length] == t[-length:start]:
                    # Update the longest palindrome length
                    longest_palindrome = max(longest_palindrome, length)

        return longest_palindrome

