class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        prefix_sum = [0] * (n + 1)
        for i in range(1, n + 1):
            prefix_sum[i] = prefix_sum[i - 1] + nums[i - 1]

        ans = float('inf')
        for l, r, val in queries:
            if prefix_sum[r + 1] - prefix_sum[l] < val:
                return -1
            ans = min(ans, r - l + 1 - (prefix_sum[r + 1] - prefix_sum[l] - val))

        return ans
