    def beautifulNumbers(self, l: int, r: int) -> int:
        def is_beautiful(n):
            product = 1
            sum_of_digits = 0
            while n > 0:
                digit = n % 10
                product *= digit
                sum_of_digits += digit
                n //= 10
            return product % sum_of_digits == 0

        count = 0
        for num in range(l, r + 1):
            if is_beautiful(num):
                count += 1
        return count
