class Solution:
    def maxContainers(self, n: int, w: int, maxWeight: int) -> int:
        dp = [[0] * (w + 1) for _ in range(n + 1)]

        for i in range(1, n + 1):
            for j in range(1, w + 1):
                if i == 1:
                    dp[i][j] = 1 if j <= w else 0
                else:
                    dp[i][j] = dp[i - 1][j]
                    if j >= i * w:
                        dp[i][j] = max(dp[i][j], dp[i - 1][j - i * w] + 1)

        return dp[n][w]
