import math

def count_even_odd_distance_graphs(N, P):
    def is_prime(num):
        if num <= 1:
            return False
        if num <= 3:
            return True
        if num % 2 == 0 or num % 3 == 0:
            return False
        i = 5
        while i * i <= num:
            if num % i == 0 or num % (i + 2) == 0:
                return False
            i += 6
        return True

    def count_even_odd_distance(N, M, P):
        if not is_prime(P):
            raise ValueError("P must be a prime number")

        if N % 2 != 0:
            raise ValueError("N must be an even number")

        if M > (N * (N - 1)) // 2:
            raise ValueError("M must be less than or equal to (N * (N - 1)) // 2")

        count = 0
        for m in range(M, 0, -1):
            if m % 2 == 0:
                count += 1
            else:
                count -= 1
            if count % 2 == 0:
                count = 0
            if count == 0:
                break
        return count % P

    return count_even_odd_distance(N, M, P)

