    if x == H - 1 and y == W - 1:
        return True

    # Check if the black cell is the only black cell in its row
    for i in range(H):
        if i != x and grid[i][y] == '#':
            return False

    # Check if the black cell is the only black cell in its column
    for j in range(W):
        if j != y and grid[x][j] == '#':
            return False

    # Check if the black cell is the only black cell in its subrectangle
    for i in range(x, H):
        for j in range(y, W):
            if i != x and j != y and grid[i][j] == '#':
                return False

    return True

