def solve(N, W, blocks, queries):
    # Initialize the grid
    grid = [[0] * W for _ in range(N)]

    # Place the blocks on the grid
    for x, y in blocks:
        grid[N - y - 1][x - 1] = 1

    # Simulate the movement of blocks
    for t in range(1, 10**101):
        # Check if the bottom row is filled with blocks
        if all(grid[N - y - 1][x - 1] == 1 for x in range(1, W + 1)):
            # Remove all blocks in the bottom row
            for x in range(1, W + 1):
                grid[N - 1][x - 1] = 0

        # Move the blocks one cell downward
        for y in range(N - 2, -1, -1):
            for x in range(1, W + 1):
                if grid[y][x - 1] == 1 and (y == N - 2 or grid[y + 1][x - 1] == 0):
                    grid[y][x - 1] = 0
                    grid[y + 1][x - 1] = 1

    # Answer the queries
    results = []
    for t, a in queries:
        if t % 2 == 0:
            results.append("Yes" if grid[t // 2][a - 1] == 1 else "No")
        else:
            results.append("Yes" if grid[t // 2][a - 1] == 1 else "No")

    return results

