def lcs_length(s1, s2):
    m, n = len(s1), len(s2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if s1[i - 1] == s2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    return dp[m][n]

def count_lcs_with_length(s, k):
    n = len(s)
    dp = [[0] * (k + 1) for _ in range(26)]

    for i in range(n):
        for j in range(k + 1):
            if j == 0:
                dp[s[i] - ord('a')][j] = 1
            else:
                dp[s[i] - ord('a')][j] = (dp[s[i] - ord('a')][j - 1] + dp[s[i] - ord('a')][(j - 1) % 26]) % 998244353

    return dp[s[0] - ord('a')][k]

def main():
    n, m = map(int, input().split())
    s = input()

    ans = [0] * (n + 1)
    for k in range(n + 1):
        ans[k] = count_lcs_with_length(s, k)

    print(' '.join(map(str, ans)))

