    for i in range(n):
        for j in range(i + 1, n):
            k1, faces1 = dice[i]
            k2, faces2 = dice[j]
            common_faces = set(faces1) & set(faces2)
            if common_faces:
                common_prob = max(common_prob, len(common_faces) / (k1 * k2))
