from collections import defaultdict

def is_alkane(graph):
    if len(graph) != len(graph[1]) + 1:
        return False
    degree = defaultdict(int)
    for u, v in graph:
        degree[u] += 1
        degree[v] += 1
    if max(degree.values()) > 4:
        return False
    if min(degree.values()) < 1:
        return False
    return True

def max_alkane_subgraph(graph):
    if not is_alkane(graph):
        return -1
    n = len(graph)
    parent = [0] * (n + 1)
    for u, v in graph:
        parent[v] = u
    stack = []
    visited = [False] * (n + 1)
    max_size = 0
    for i in range(1, n + 1):
        if not visited[i]:
            stack.append(i)
            visited[i] = True
            while stack:
                u = stack.pop()
                for v in graph[u]:
                    if not visited[v]:
                        stack.append(v)
                        visited[v] = True
                max_size = max(max_size, len(stack))
    return max_size

