    stack = []
    visited = [False] * (n + 1)
    max_size = 0
    for i in range(1, n + 1):
        if not visited[i]:
            stack.append(i)
            visited[i] = True
            while stack:
                u = stack.pop()
                for v in graph[u]:
                    if not visited[v]:
                        stack.append(v)
                        visited[v] = True
                max_size = max(max_size, len(stack))
