def min_cost(N, M, X, edges):
    graph = [[] for _ in range(N + 1)]
    for u, v in edges:
        graph[u].append(v)

    def dfs(node, parent):
        if node == N:
            return 0

        min_cost = float('inf')
        for neighbor in graph[node]:
            if neighbor != parent:
                cost = dfs(neighbor, node)
                min_cost = min(min_cost, cost + 1)

        return min_cost

    return dfs(1, -1) + X

