def find_min_good_sequence(N, M, X, Y, Z):
    # Create a dictionary to store the XOR results for each pair (X_i, Y_i)
    xor_dict = {}

    # Calculate the XOR results for each pair (X_i, Y_i)
    for i in range(M):
        xor_dict[(X[i], Y[i])] = X[i] ^ Y[i]

    # Check if there is a valid good sequence
    for i in range(N):
        if xor_dict[(X[i], Y[i])] != Z[i]:
            return -1

    # Find the minimum sum of the good sequence
    min_sum = float('inf')
    min_sequence = []

    # Generate all possible good sequences and find the one with the minimum sum
    for i in range(1 << N):
        sequence = [0] * N
        for j in range(N):
            if i & (1 << j):
                sequence[j] = 1
        if all(xor_dict[(X[i], Y[j])] == Z[j] for i in range(M)):
            if sum(sequence) < min_sum:
                min_sum = sum(sequence)
                min_sequence = sequence

    return ' '.join(map(str, min_sequence))

