def find_min_good_sequence(N, M, X, Y, Z):
    # Check if it's possible to form a good sequence
    if not all(0 <= x <= N and 0 <= y <= N and 0 <= z <= 10**9 for x, y, z in zip(X, Y, Z)):
        return -1

    # Create a frequency dictionary to count the occurrences of each number in X and Y
    freq_X = {}
    freq_Y = {}
    for x, y in zip(X, Y):
        freq_X[x] = freq_X.get(x, 0) + 1
        freq_Y[y] = freq_Y.get(y, 0) + 1

    # Check if the XOR of any two numbers in X and Y can match Z
    for i in range(M):
        if freq_X[X[i]] == 0 or freq_Y[Y[i]] == 0:
            return -1

    # Find the minimum sum of the good sequence
    min_sum = float('inf')
    best_sequence = []

    for i in range(N):
        for j in range(N):
            if i != j and freq_X[i] > 0 and freq_Y[j] > 0:
                if (i ^ j) == Z[i]:
                    freq_X[i] -= 1
                    freq_Y[j] -= 1
                    if sum(best_sequence) > sum(best_sequence + [i, j]):
                        best_sequence = [i, j]
                    min_sum = min(min_sum, sum(best_sequence))

    if min_sum == float('inf'):
        return -1

    return ' '.join(map(str, best_sequence))

