def is_valid_decomposition(N, K, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, path):
        if len(path) == K:
            return True
        for neighbor in graph[node]:
            if neighbor not in path:
                path.append(neighbor)
                if dfs(neighbor, path):
                    return True
                path.pop()
        return False

    for i in range(1, N + 1):
        if not dfs(i, [i]):
            return False
    return True

def main():
    N, K, edges = read_input()
    if is_valid_decomposition(N, K, edges):
