import sys
from collections import defaultdict

def read_input():
    input = sys.stdin.read
    data = input().split()
    N, K = int(data[0]), int(data[1])
    edges = defaultdict(list)
    for _ in range(N - 1):
        u, v = map(int, data[2 * _ + 1:2 * _ + 3])
        edges[u].append(v)
        edges[v].append(u)
    return N, K, edges

def can_decompose(N, K, edges):
    # Create a list of all vertices
    vertices = list(range(1, N + 1))

    # Create a list of all edges
    edge_list = [(u, v) for u in vertices for v in edges[u] if u < v]

    # Create a list of all paths
    paths = []
    for i in range(N):
        path = [i + 1]
        while path[-1] != 1:
            path.append(edges[path[-1]][0])
        paths.append(path)

    # Check if the paths can be decomposed into N paths each of length K
    if len(paths) != N:
        return False
    for i in range(N):
        if len(paths[i]) != K:
            return False
    for i in range(N):
        for j in range(K - 1):
            if (paths[i][j], paths[i][j + 1]) not in edge_list:
                return False
    return True

def main():
    N, K, edges = read_input()
    if can_decompose(N, K, edges):
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()
