    for i in range(len(path) - 1):
        if graph[path[i]][path[i + 1]] == 0:
            return False
    return True

def decompose_tree(N, K, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    path = [1]
    visited = [False] * (N * K + 1)
    visited[1] = True

    while len(path) < N * K:
        current = path[-1]
        for neighbor in graph[current]:
            if not visited[neighbor]:
                path.append(neighbor)
                visited[neighbor] = True
                break

    if len(path) != N * K:
        return False

    for i in range(1, N * K):
        if not is_valid_path(graph, path[i:i + K]):
            return False

    return True

