        paths.append(path)

    # Check if the paths can be decomposed into N paths each of length K
    if len(paths) != N:
        return False
    for i in range(N):
        if len(paths[i]) != K:
            return False
    for i in range(N):
        for j in range(K - 1):
            if (paths[i][j], paths[i][j + 1]) not in edge_list:
                return False
    return True

