from collections import defaultdict

def solve(N, M, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * (N + 1)
    parent = [0] * (N + 1)
    odd_cycle = False

    def dfs(node, prev):
        visited[node] = True
        parent[node] = prev

        for neighbor in graph[node]:
            if not visited[neighbor]:
                if dfs(neighbor, node):
                    odd_cycle = True
                    return True
            elif neighbor != prev:
                if parent[node] != neighbor:
                    odd_cycle = True
                    return True

        return False

    for i in range(1, N + 1):
        if not visited[i]:
            if dfs(i, 0):
                odd_cycle = True
                break

    if odd_cycle:
        return "Aoki"
    else:
        return "Takahashi"

