import math

def is_400_number(n):
    if n < 400:
        return False
    factors = set()
    for i in range(2, int(math.sqrt(n)) + 1):
        while n % i == 0:
            factors.add(i)
            n //= i
    if n > 1:
        factors.add(n)
    return len(factors) == 2 and all(factors.count(p) % 2 == 0 for p in factors)

def largest_400_number(n):
    for i in range(n, 0, -1):
        if is_400_number(i):
            return i

Q = int(input())
