import sys
from collections import defaultdict
from math import comb

def solve(H, W, A, Q, sh, sw, changes):
    grid = [[0] * W for _ in range(H)]
    grid[sh-1][sw-1] = A[sh-1][sw-1]

    for d, a in changes:
        if d == 'L':
            for i in range(H):
                grid[i][sw-1] = a
        elif d == 'R':
            for i in range(H):
                grid[i][sw] = a
        elif d == 'U':
            for j in range(W):
                grid[sh-1][j] = a
        elif d == 'D':
            for j in range(W):
                grid[sh][j] = a

    paths = defaultdict(int)
    for i in range(H):
        for j in range(W):
            paths[(i, j)] = grid[i][j]

    def dfs(i, j, path):
        if i == H-1 and j == W-1:
            paths[path] *= grid[i][j]
            return
        if i < H-1:
            dfs(i+1, j, path + (i+1, j))
        if j < W-1:
            dfs(i, j+1, path + (i, j+1))

    dfs(0, 0, (0, 0))

    return sum(paths.values()) % 998244353

