def min_operations(N, M, S, T, edges):
    if S == T:
        return -1
    visited = [False] * (N + 1)
    queue = [(S, 0)]
    while queue:
        current, moves = queue.pop(0)
        if current == T:
            return moves
        visited[current] = True
        for neighbor in edges[current]:
            if not visited[neighbor]:
                queue.append((neighbor, moves + 1))
    return -1

