import sys

def read_input():
    input = sys.stdin.read
    data = input().split()
    N, M, S, T = map(int, data)
    edges = []
    for _ in range(M):
        u, v = map(int, input().split())
        edges.append((u, v))
    return N, M, S, T, edges

def find_path(N, M, S, T, edges):
    graph = [[] for _ in range(N + 1)]
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    visited = [False] * (N + 1)
    stack = [S]
    path = []

    while stack:
        node = stack.pop()
        if not visited[node]:
            visited[node] = True
            path.append(node)
            for neighbor in graph[node]:
                if not visited[neighbor]:
                    stack.append(neighbor)

    if T not in path:
        return -1

    path = path[::-1]
    operations = 0

    for i in range(len(path) - 1):
        u, v = path[i], path[i + 1]
        if u == S and v == T:
            continue
        if u == S:
            operations += 1
        elif v == S:
            operations += 1
        else:
            operations += 1

    return operations

def main():
    N, M, S, T, edges = read_input()
    result = find_path(N, M, S, T, edges)
    print(result)

