def solve(W, H, L, R, D, U):
    MOD = 998244353
    dp = [[0] * (W + 1) for _ in range(H + 1)]
    dp[0][0] = 1

    for i in range(1, H + 1):
        for j in range(1, W + 1):
            if i < D or i > U or j < L or j > R:
                dp[i][j] = 0
            else:
                dp[i][j] = (dp[i - 1][j] + dp[i][j - 1]) % MOD

