from collections import defaultdict

def build_graph(n, weights, intervals):
    graph = defaultdict(list)
    for i in range(n):
        for j in range(i+1, n):
            if not intervals[i].intersection(intervals[j]):
                graph[i].append(j)
                graph[j].append(i)
    return graph, weights

def dijkstra(graph, weights, start):
    n = len(graph)
    distances = [float('inf')] * n
    distances[start] = 0
    pq = [(0, start)]
    while pq:
        dist, u = heapq.heappop(pq)
        if dist > distances[u]:
            continue
        for v in graph[u]:
            new_dist = dist + weights[v]
            if new_dist < distances[v]:
                distances[v] = new_dist
                heapq.heappush(pq, (new_dist, v))
    return distances

def solve(n, weights, intervals, queries):
    graph, weights = build_graph(n, weights, intervals)
    results = []
    for s, t in queries:
        distances = dijkstra(graph, weights, s-1)
        if distances[t-1] == float('inf'):
            results.append(-1)
        else:
            results.append(distances[t-1])
    return results

