from collections import defaultdict

def build_graph(N, W, L, R):
    graph = defaultdict(list)
    for i in range(N):
        for j in range(i+1, N):
            if not (L[i] <= R[j] and L[j] <= R[i]):
                graph[i].append(j)
                graph[j].append(i)
    return graph

def find_min_weight_path(graph, s, t, W):
    if s == t:
        return W[s]
    visited = set()
    queue = [(s, 0)]
    while queue:
        node, weight = queue.pop(0)
        if node in visited:
            continue
        visited.add(node)
        for neighbor in graph[node]:
            if neighbor == t:
                return weight + W[neighbor]
            queue.append((neighbor, weight + W[neighbor]))
    return -1

