def count_distinct_strings(N, S):
    if N == 0:
        return 1
    if N == 1:
        return 1 if S == '()' else 0

    # Check if the string is already a valid parenthesis sequence
    if S == '()' or S == '()()':
        return 2

    # Check if the string can be formed by concatenating valid parenthesis sequences
    if S[0] == '(' and S[-1] == ')':
        return 2

    # Check if the string can be formed by concatenating non-empty valid parenthesis sequences
    if S[0] == '(' and S[-1] == ')':
        return 2

    # Check if the string can be formed by reversing a valid parenthesis sequence
    for i in range(N):
        for j in range(i+1, N):
            if S[i:j+1] == '()' or S[i:j+1] == '()()':
                return 2

    # If none of the above conditions are met, the string cannot be formed
    return 0

