def solve(N, S):
    MOD = 998244353
    graph = defaultdict(list)
    for i in range(2*N):
        if S[i] == 'W':
            graph[i].append(i+1)
        else:
            graph[i+1].append(i)

    def dfs(node, visited):
        visited.add(node)
        for neighbor in graph[node]:
            if neighbor not in visited:
                dfs(neighbor, visited)

    def is_strongly_connected():
        visited = set()
        dfs(0, visited)
        return len(visited) == 2*N

    def count_ways():
        if not is_strongly_connected():
            return 0

        # Count the number of ways to partition the vertices into N pairs
        # such that the final graph is strongly connected
        ways = 0
        for i in range(N):
            for j in range(i+1, N):
                if S[2*i] == 'W' and S[2*j] == 'B':
                    ways += comb(N, 2)

        return ways % MOD

    return count_ways()

