def find_shortest_path(graph, start, end):
    distances = {node: float('inf') for node in graph}
    distances[start] = 0
    queue = [start]
    visited = set()

    while queue:
        current = queue.pop(0)
        visited.add(current)

        for neighbor in graph[current]:
            if neighbor not in visited:
                distance = distances[current] + 1
                if distance < distances[neighbor]:
