def build_graph(n, roads):
    graph = defaultdict(list)
    for u, v, w in roads:
        graph[u].append((v, w))
        graph[v].append((u, w))
    return graph

def find_shortest_path(graph, start, end):
    queue = [(start, 0)]
    visited = set()
    while queue:
        node, distance = queue.pop(0)
        if node == end:
            return distance
        if node in visited:
            continue
        visited.add(node)
        for neighbor, weight in graph[node]:
            queue.append((neighbor, distance + weight))
    return float('inf')

def can_set_strengths(graph, start, end, strengths):
    stamina = 0
    for i in range(start, end + 1):
        stamina += strengths[i - 1]
        if stamina < 0:
            return False
    return True

def solve(n, m, q, start, end, strengths):
    graph = build_graph(n, roads)
    for _ in range(q):
        L, R = start, end
        while L < R:
            mid = (L + R) // 2
            if can_set_strengths(graph, start, mid, strengths):
                R = mid
            else:
                L = mid + 1
